import * as React from "react";
import Box from "@mui/material/Box";
import Drawer from "@mui/material/Drawer";
import CssBaseline from "@mui/material/CssBaseline";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import List from "@mui/material/List";
import Typography from "@mui/material/Typography";
import Divider from "@mui/material/Divider";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import Store from "@mui/icons-material/Store";
import PermIdentity from "@mui/icons-material/PermIdentity";
import Login from "../Login/Login";
import { useState } from "react";
import { Button } from "@mui/material";

const drawerWidth = 240;

const SideBar = () => {
  const [myID, setMyID] = useState("");
  const [isLogin, setIsLogin] = useState(false);

  const LoginHandler = () => {
    setIsLogin(true);
  };

  const LogoutHandler = () => {
    setIsLogin(false);
  };

  const MyIDHander = (ID) => {
    setMyID(ID);
  };

  return (
    <Box sx={{ display: "flex" }}>
      <CssBaseline />
      <AppBar
        position="fixed"
        sx={{ width: `calc(100% - ${drawerWidth}px)`, ml: `${drawerWidth}px` }}
      >
        <Toolbar>
          <Typography variant="h6" noWrap component="div">
            homepage
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          "& .MuiDrawer-paper": {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {["내 정보", "REC 구매", "REC 판매"].map((text, index) => (
            <ListItem key={text} disablePadding>
              <ListItemButton disabled={!isLogin}>
                <ListItemIcon>
                  {index === 0 && <PermIdentity />}
                  {index === 1 && <Store />}
                  {index === 2 && <Store />}
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
        <Divider />
        {!isLogin && <Login onLogin={LoginHandler} onID={MyIDHander} />}
        {isLogin && <p>{myID}</p>}
        {isLogin && <Button onClick={LogoutHandler}>Logout</Button>}
      </Drawer>
      <Box
        component="main"
        sx={{ flexGrow: 1, bgcolor: "background.default", p: 3 }}
      >
        <Toolbar />
        <Typography paragraph>설명이 들어갈 예정</Typography>
        <Typography paragraph>설명이 들어갈 예정</Typography>
      </Box>
    </Box>
  );
};

export default SideBar;
